//2)Continue Statement


package javafullcourse;
import java.util.Scanner;
public class cantrolflow {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          for(int i=1;i<=10;i++) {
        	  if(i==6 )
        		  continue;
        	  System.out.println("The No Is : "+i);
          }
	}

}